Autodesk.Viewing.MolViewer.MolInstance.prototype._turnOffVisibility = function _turnOffVisibility(rep) {
    if(rep) {
        if (this._3DViews[rep]) {
            this._3DViews[rep].hide();
        }
    }else{
        for(var view in this._3DViews){
            this._3DViews[view].hide();
        }
    }
};

