    var offset = this.offset;
    var off  = {x:0,y:0,z:0};// this.renderModel.myData.globalOffset; //for now zero leaving in case it comes back
    var loc = atomID * 3;
    var x = this.positions[loc] - off.x;
    var y = this.positions[loc + 1] - off.y;
