Autodesk.Viewing.MolViewer.InspectorView.prototype.getUniqueColorings = function getUniqueColorings(colorings) {
    if (!colorings) {
        return false;
    }

    var retObj = colorings,
        retArray = [], key;
    for (key in retObj) {
        if (retObj.hasOwnProperty(key)) {
            retArray.push(retObj[key]);
        }
    }
    return retArray;
};

